import { useState, useEffect } from 'react'
import Input from '../form/Input'
import Select from '../form/Select'
import SubmitButton from '../form/SubmitButton'
import styles from './FormProjeto.module.css'
function FormProjeto({submitText}){
    const [categorias, setCategorias] = useState([])
    
    useEffect(() => {
            fetch('http://localhost:5000/categories', {
            method:'GET',
            headers: {
                        'Content-Type': 'Application/json',
                    },
        }).then((resp) => resp.json())
        .then((data => {
            setCategorias(data)
        }))
        .catch((err) => console.log(err))
        }, []
    )
    

    return(
        <form className={styles.form}>
            <Input type="text"
                   placeholder="Insira o nome do projeto"
                   name="nomeDoProjeto"
                   text="Nome do Projeto"/>
            
            <Input type="number"
                   placeholder="Insira o orçamento total do projeto"
                   name="orçamentoDoProjeto"
                   text="Orçamento do Projeto"/>

            <Select text="Selecione a categoria" name="categoria_id" options={categorias}/>
            <SubmitButton text={submitText}/>
        </form>
    )
} export default FormProjeto
